-- 20180417113300_Msi132FixInitialSseCommandNames.sql

-- Normalize names of all initial sse commands that reference single initial configs
UPDATE initial_sse_commands
SET display_name = IFNULL((select d.full_name||' '||ic.name from initial_configurations ic 
            INNER JOIN devices d on ic.device_id = d.id
			WHERE initial_sse_commands.params='{"configIds":["#INITIALCONFIG_'||ic.id||'"]}'),initial_sse_commands.display_name)
WHERE command_type='deploy_configs';
-- Same, but for all initial sse commands that reference single default device configs
UPDATE initial_sse_commands
SET display_name = IFNULL((select d.full_name||' Default' from devices d 
            WHERE initial_sse_commands.params='{"configIds":["#DEFAULTCONFIG_DEVICE_'||d.id||'"]}'),initial_sse_commands.display_name)
WHERE command_type='deploy_configs';

